template.script:
document.addEventListener('DOMContentLoaded', function() {
const snippet = document.getElementById('snippet1');
const sentinel = document.querySelector('.sentinel');

// Gözlemci tanımlama
const observer = new IntersectionObserver((entries, observer) => {
entries.forEach(entry => {
if (entry.isIntersecting) {
snippet.classList.add('scroll-active'); // Scroll sonrası normal pozisyona geç
console.log("scroll-active sınıfı eklendi.");

// Gözlemciyi durdur, artık scroll-active sınıfı kaldırılmayacak
observer.disconnect();
}
});
}, { threshold: 0.1 });

// Gözlemciyi sentinel elemanına ata
observer.observe(sentinel);
});

